import React, { Component } from "react";
import * as api from "./Api";

class Voter extends Component {
  handleClick = event => {
    this.props.updateVote(1);
    api.changeVotesById(1);
  };
  render() {
    const { votes, articleId } = this.props;
    return (
      <div>
        <h2>Current Votes:{votes}</h2>
        <button onClick={this.handleClick}>
          <span id={articleId} value="upvote">
            Upvote △
          </span>
        </button>
        <button>
          <span value="downvote">Downvote ▽</span>
        </button>
      </div>
    );
  }
}

export default Voter;
