import React, { Component } from "react";
import * as api from "./Api";

class Voter extends Component {
  state = {
    voted: false
  };
  handleClick = increment => {
    const { articleId } = this.props;
    this.setState({ voted: true });
    api.changeVotesById(articleId, increment);
  };
  render() {
    let { votes, articleId } = this.props;
    if (this.state.voted === true) {
      votes++;
    }

    return (
      <div>
        <h2>Current Votes:{votes}</h2>
        <button onClick={() => this.handleClick(1)} value="upvote">
          <span>Upvote △</span>
        </button>
        <button
          onClick={() => {
            this.handleClick(-1);
          }}
        >
          <span id={articleI}>Downvote ▽</span>
        </button>
      </div>
    );
  }
}

export default Voter;
