import React, { Component } from 'react';

class SortArticles extends Component {
    render() {
        return (
            <form>
            <label>Sort By</label>            
            <input type = "radio"
                   name = "sortBy"
                   id = "orderTitle"
                   value = "title"/>
            <label for = "title">Title</label>
            <input type = "radio"
                   name = "sortBy"
                   id = "orderAuthor"
                   value = "author" />
            <label for = "author">Author</label>
            <input type = "radio"
                   name = "sortBy"
                   id = "orderNewest"
                   value = "newest" />
            <label for = "newest">Newest</label>
            <input type = "submit"/>
          </form>
        );
    }
}

export default SortArticles;
