import React from "react";
import ArticlesList from "./ArticlesList";

function ArticlesByAuthor(props) {
  return (
    <div>
      <h2>{`Here's all our articles by ${props.topic}`}</h2>
      <ArticlesList topic={props.topic} />
    </div>
  );
}

export default ArticlesByTopic;
