import React from "react";

function CommentCard(props) {
  const { comment } = props;
  return (
    <div className="commentCard">
      <h3 id="commentHeader">By: {comment.author}</h3>
      <h3>At: {comment.created_at}</h3>

      <body id="commentBody">{comment.body}</body>
    </div>
  );
}

export default CommentCard;
