import React, { Component } from "react";
import * as api from "./Api"

class CommentAdder extends Component {
  state = {
    valid: false,
    input: "What do you think?",
    user: this.props.user
  };

  handleChange = event => {
    this.setState({ input: event.target.value });
  };
  handleClick = () => {
    this.setState({ input: "" });
  };
  handleSubmit = event => {
      event.preventDefault()
      const {user, comment} = this.state
      const requestItem = {username: user, body: comment}
      api.postComment(requestItem).then((res)=>{console.log(res)})
    ;
  };
  render() {
    return (
      <form className="commentForm" value={this.state.input} onSubmit={this.handleSubmit}>
        <p>Add Comment: </p>
        <h2>{`Posting as ${this.props.user} `} </h2>
        <input
          type="text"
          onChange={this.handleChange}
          onClick={this.handleClick}
        />
        <button id="postCommentButton" type="submit">
          Post
        </button>
      </form>
    );
  }
}

export default CommentAdder;
