import React, { Component } from "react";
import { getArticles } from "./Api";
import Loading from "./Loading";
import ArticleAdder from "./ArticleAdder";
import ArticlesList from "./ArticlesList";
import SortArticles from "./SortArticles";

export default class HomePage extends Component {
  state = {
    articles: [],
    isLoading: true
  };
  componentDidMount() {
    getArticles().then(res =>
      this.setState({ articles: res.data.articles, isLoading: false })
    );
  }

  componentDidUpdate(currentState){
      const copy = [...currentState.articles]
     const sorted = copy.sort(function(a, b) {
        var textA = a.title.toUpperCase();
        var textB = b.title.toUpperCase();
        return (textA < textB) ? -1 : (textA > textB) ? 1 : 0;
    })
  this.setState({articles: sorted})
  }

  orderArticles(orderBy){
    this.componentDidUpdate
  }


  render() {
    console.log(this.state)
    if (this.state.isLoading === true) {
      return <Loading />;
    }
    const { articles } = this.state;

    return (
      <div>
        <ArticleAdder />
        <SortArticles articles={articles} orderArticles={this.orderArticles} />
        <ArticlesList articles={articles} />
      </div>
    );
  }
}
