import React, { Component } from "react";
import ArticleCard from "./ArticleCard";
import * as api from "./Api";
import Loading from "./Loading";


class ArticlesList extends Component {
  state = {
    articles: [],
    isLoading: true
  };

  fetchArticles = res => {
    console.log()
    this.setState({ articles: res.data.articles, isLoading: false })
  };
  componentDidMount() {
    const { topic, orderBy, author } = this.props;
    api
      .getArticles(topic, orderBy, author)
      .then(res => this.fetchArticles(res));
  }
  componentDidUpdate(prevProps) {
    const { topic, orderBy, author } = this.props;

    if (topic !== prevProps.topic) {
      api.getArticles(topic).then(res => this.fetchArticles(res));
    }
    if (orderBy !== prevProps.orderBy) {
      api.getArticles(null, orderBy).then(res => this.fetchArticles(res));
    }
    if (author !== prevProps.author) {
      api.getArticles(null, null, author).then(res => {
        this.fetchArticles(res)
      });
    }
  }

  render() {
    if (this.state.isLoading === true) {
      return <Loading />;
    }
    const { articles } = this.state;

    return (
      <ul>
        {articles.map(article => (
          <li key={article.article_id}>
            <ArticleCard article={article} />
          </li>
        ))}
      </ul>
    );
  }
}

export default ArticlesList;
