import React from "react";
import DeleteComment from "./DeleteComment";
import Voter from "./Voter"
import { getArticleComments } from "./Api";

function CommentCard(props) {
  const { comment, user, removeComment } = props;
console.log(comment)
  return (
    <div className="commentCard">
      <header id="commentHeader">
        <h3 id="commenth3">By: {comment.author}</h3>
        <h3 id="commenth3">At: {comment.created_at}</h3>
      </header>
      <main id="commentBody">{comment.body} <Voter votes={comment.votes} />{user === comment.author && (
        <DeleteComment articleId={props.articleId} commentId={comment.comment_id} removeComment={removeComment} />
      )}</main>


    </div>
  );
}

export default CommentCard;
