import React, { Component } from "react";
import axios from "axios";
import { getTopics } from "./Api";

export default class TopicsPage extends Component {
  state = {
    topics: [],
    isLoading: true
  };

  componentDidMount() {
    getTopics().then(res => this.setState({ topics: res.data.topics }));
  }

  render() {
    const { topics } = this.state;

    return (
      <div>
        <ul id = "topics">
          {topics.map(topic => (
            <li key={topic.slug}>{topic.slug}</li>
          ))}
        </ul>
      </div>
    );
  }
}
