import React from "react";

const handleClick = event => {
  console.log(event);
};

function SortArticles() {
  return (
    <label>
      Sort By:{" "}
      <select>
        <option value="title" onSelect={handleClick}>
          Title
        </option>
        <option value="author" onSelect={handleClick}>
          Author
        </option>
        <option value="newest" onSelect={handleClick}>
          Newest
        </option>
      </select>
    </label>
  );
}

export default SortArticles;
