import React from "react";

function CommentCard(props) {
  const { comment } = props;
  return (
    <div className="commentCard">
      <header id="commentHeader">By: {comment.author}
      At: {comment.created_at}</header>

      <body class>{comment.body}</body>
    </div>
  );
}

export default CommentCard;
