import React, { Component } from "react";
import {Link} from "@reach/router"
import { getSingleArticle } from "./Api";
import CommentFetcher from "./CommentFetcher"
import Loading from "./Loading"
import Voter from "./Voter"
export default class SingleArticle extends Component {
  state = {
    article: {},
    isLoading: true
  };
  componentDidMount() {
    const { articleId } = this.props;
    getSingleArticle(articleId).then(res =>
      this.setState({ article: res.data.article, isLoading: false })
    );
  }
updateVote=(id, increment)=>{
  this.setState((currentState)=> {
    if(currentState.article.article_id === id){
      currentState.article.article_id ++
    }
    console.log(currentState.article)
  }
  )
}
  render() {
    if(this.state.isLoading === true){
      return <Loading/>
    }


    const { article } = this.state;
     console.log(article)
    return (
      <div id="singleArticleBox">
       <Link to = {`/topics/${article.topic}`}> <p id="topic">{article.topic}</p></Link>
        <p id="title">{article.title}</p>
        <Link to = {`/authors/${article.author}`}><p id="author">{article.author}</p></Link>
        <p id="body">{article.body}</p>
        <Voter votes={article.votes} articleId={article.article_id} updateVote={this.updateVote}/>
       <CommentFetcher articleId = {article.article_id} user ={this.props.user}/>
      </div>
    );
  }
}
