import React, { Component } from "react";

class DeleteComment extends Component {
  handleClick = (event) => {
      console.log(event.target.id)
    this.props.removeComment()
  };

  render() {
    return (
      <button id={this.props.id} onClick={this.handleClick}>
        Delete your comment?
      </button>
    );
  }
}
export default DeleteComment;
