import React, { Component } from "react";
import * as api from "./Api";

class Voter extends Component {
    state = {
        voted: false
    }
  handleClick = increment => {
      console.log(increment)
    const { id } = event.target;
    this.setState({voted: true})
    api.changeVotesById(id, 1);
  };
  render() {
   let { votes, articleId } = this.props;
    if(this.state.voted === true){
        votes++
    }

    return (
      <div>
        <h2>Current Votes:{votes}</h2>
        <button onClick={this.handleClick} value="upvote">
          <span id={articleId}>Upvote △</span>
        </button>
        <button onClick ={()=>this.handleClick(-1)}>
          <span value="downvote">Downvote ▽</span>
        </button>
      </div>
    );
  }
}

export default Voter;
