  return (
    <label>
      Sort By:{" "}
      <form>
        <label>
          Title: <input type="radio" value="Title" />
        </label>
        <label>
          Author: <input type="radio" value="Author" />
        </label>
        <label>
            Newest: 
          <input type="radio" value="Newest" onClick={handleClick} />
        </label>
        <input type = "submit"/>
      </form>
    </label>
  );
}

