import React, { Component } from "react";
import { getArticles } from "./Api";
import ArticlesList from "./ArticlesList";

export default class ArticlesByTopic extends Component {
  state = {
    articles: [],
    isLoading: true
  };

  componentDidMount() {
    const { topic } = this.props;
    getArticles(topic).then(res =>
      this.setState({articles: res.data.articles})
    );
  }

  componentDidUpdate(prevProps) {
    if (prevProps.topic !== this.props.topic) {
      getArticles(this.props.topic).then(res => {
        this.setState((currentState)=>{
          const topicArticles = res.data.articles
          return currentState.articles = topicArticles
        })

      });
    }
  }

  render() {
    const { articles } = this.state;
    const { topic } = this.props;

    return (
      <div>
        <h2>{`Here's all our articles on ${this.topic}`}</h2>
        <ArticlesList articles={articles} topic={topic} />
      </div>
    );
  }
}
