import React, { Component } from "react";
import { getArticles } from "./Api";
import ArticlesList from "./ArticlesList";

export default class ArticlesByAuthor extends Component {
  state = {
    articles: [],
    isLoading: true
  };
  componentDidMount() {
    const { author } = this.props;
    console.log(author)
    getArticles(null, null, author).then(res =>
      this.setState({ articles: res.data.articles, isLoading: false })
    );
  }

  componentDidUpdate(prevProps) {
    if (prevProps.autho !== this.props.author) {
      const { author } = this.props;
      getArticles(null, null, author).then(res => {
        this.setState({ articles: res.data.articles, isLoding: false });
      });
    }
  }
  render() {
    const { articles } = this.state;
    const { author } = this.props;
    return (
      <div>
        <h2>{`Here's all our articles by ${author}`}</h2>
        <ArticlesList articles={articles} author={author} />
      </div>
    );
  }
}
