import React, { Component } from "react";

class SortArticles extends Component {
  handleClick = event => {
    event.preventDefault();
    console.log(event);
  };
  render() {
    return (
      <form onSubmit={this.handleClick}>
        <label>Sort By</label>
        <input type="radio" name="sortBy" id="orderTitle" value="title" onChange/>
        <label htmlFor="title">Title</label>
        <input type="radio" name="sortBy" id="orderAuthor" value="author" />
        <label htmlFor="author">Author</label>
        <input type="radio" name="sortBy" id="orderNewest" value="newest" />
        <label htmlFor="newest">Newest</label>
        <input type="submit" />
      </form>
    );
  }
}

export default SortArticles;
