import React, { Component } from "react";
import * as api from "./Api"

class Voter extends Component {
  handleClick = (event) => {
      api.changeVotesById()
  };
  render() {
    const { votes } = this.props;
    return (
      <div>
        <h2>Current Votes:{votes}</h2>
        <button onClick={this.handleClick}>
          <span>Upvote △</span>
        </button>
        <button>
          <span>Downvote ▽</span>
        </button>
      </div>
    );
  }
}

export default Voter;
