import React, { Component } from "react";
import { getTopics } from "./Api";

export default class ArticlesByTopic extends Component {
  state = {
    articles: [],
    isLoading: true

  };
  componentDidMount() {
    const { topic } = this.props;
    getTopics(topic);
  }

  render() {
    return <div></div>;
  }
}
